css:
@-moz-document domain("weather.yahoo.co.jp")
{


#header,
#headNav,
#imatenmd,
#radarmd,
#ctlink,
#sns_btn,
#wrnrpt,
iframe,
#bnr,
#video,
#yjImgSLink,
#stcftw2a,
aside,
[id^=ydn]

{
    display: none !important;
}

.timetable {
    margin: 0 3px;
}

/*** DEFAULT=16,16,17,17,17,17 ***/
.timetable col:nth-child(1) {
    width: 15% !important;
}
.timetable col:nth-child(2) {
    width: 21% !important;
}
.timetable col:nth-child(3) {
    width: 16% !important;
}
.timetable col:nth-child(4) {
    width: 16% !important;
}
.timetable col:nth-child(5) {
    width: 15% !important;
}
.timetable col:nth-child(6) {
    width: 17% !important;
}

.timetable .tenki {
    display: inline-block;
}
/*
.timetable .precipitation span {
    display: block;
}
*/
#tomorrowForcast + .switch {
    display: none !important;
}

.precipitation > span,
.wind > .direction
{
    display: none !important;
}
.precipitation:after {
    content: 'mm';
    margin-left: -0.3em;
}

}
