css:
@-moz-document domain("huboard.com") {
* {
    text-shadow: none !important;
}

div {
    background-color: #333;
    color: #eee;
}

a, .drop-shadow {
    color: inherit !important;
    background-color: inherit !important;
}

.card {
    border-color: #aaa;
    background-color: #333;
}

.board-wrapper .board::-webkit-scrollbar-track-piece, .column .column-contents .cards::-webkit-scrollbar-track-piece
{
    background: #aaa;
}

.hb-card-tray
{
    background: inherit !important;
    background-color: inherit !important;
    border-color: inherit !important;
}

.fullscreen-wrapper
{
    background-color: transparent;
}

.fullscreen-card
{
    background-color: inherit;
}

.navbar, .navbar-inner, #footer
{
    background-color: #333 !important;
    background-image: none !important;
}
  
.card-labels.compact .card-label-wrapper .card-label.active.large {
    display: inherit;
}

.card-labels.compact .card-label-wrapper .card-label.active.small {
    display: none;
}
}
