css:
@-moz-document url-prefix("https://laravel.com/docs"), url-prefix("https://www.laravel.com/docs") {
* {font-size: 24px !important; background: #333; text-shadow: none;}
.container{width: 100%; max-width: 100%; margin: 0;}
.docs article h2 a, nav.main ul.main-nav > li > a, * {color: #fff;}
nav.main *, nav.main, footer.main *, footer.main {background: #222;}
.browser-window, .browser-window .top-bar, code, .token {background: transparent;}
:not(pre) > code[class*="language-"], code[class^=" language"], pre[class^=" language"], .browser-window .window-content pre[class*="language-"]{background: #222; color: #fff; }
.token.operator {color: #fff;}
.token.comment {color: #777;}
.token.keyword {color: #CC7832;}
.token.function {color: #FFC660;}
.token.scope {color: #A987C6;}
.token.string {color: #6A8759;}
.token.variable {color: #9876AA;}
code[class*="language-"], pre[class*="language-"] {text-shadow: none;}
.dropdown-menu > li > a {color: inherit;}
blockquote * {background: inherit;}
}
