css:
span[style^="color: transparent"],
span[style^="color: Transparent"],
span[style^="color: TRANSPARENT"] {
	color: #000000;
	background-color: #000000;
	padding: 0px 2px;
}

span[style^="color: transparent"]:hover,
span[style^="color: Transparent"]:hover,
span[style^="color: TRANSPARENT"]:hover {
	color: #CCCCCC !important;
}
