css:
@-moz-document domain("weheartit.com") {
/* weheartit theme by midnight */

.panel-navbar {
    background: hsl(0, 0%, 2%);
}

.panel-notice {
    background-color: #af413c;
    color: hsl(0,0%,40%);
}

article, aside, details, figcaption, figure, footer, header, hgroup, main, nav, section, summary {
    background: url(http://i.imgur.com/X5MYeHh.png);
}

.panel, .btn, input, textarea, select, button {
    background: hsl(0, 0%, 2%);
    box-shadow: hsl(0, 0%, 2%);
}

textarea:hover, select:hover, button:hover {
    box-shadow: 0 1px 2px 2px hsl(0,0%,6%);
    background: hsl(0,0%,6%);
}

.navbar-light .dropdown {
    background: #050505;
}

.dropdown-menu-light>li>a:hover {
	background: #111;
}

.bg-offwhite {
    background-color: hsl(0,0%,6%) !important;
}

.bg-offwhite:hover {
    background-color: hsl(0,0%,6%) !important;
}

.bg-info-light {
    background-color: rgb(0, 0, 0) !important;
}

html, body {
    background: url(http://i.imgur.com/X5MYeHh.png);
}

.header-content .avatar {
    box-shadow: 0 0 0 4px rgb(15, 15, 15);
    background: rgba(0,0,0,0.8);
}

.modal {
    background: hsla(0, 0%, 2%, 0.85);
}

.dropdown:hover {
    background: transparent;
}

.bg-success-light {
    background-color: rgba(0,0,0,0.8) !important;
}

#loader, #lightbox {
    background-color: hsla(0, 0%, 2%, 0.81);
}

.floating-window .floating-window-content {
    background: hsl(0, 0%, 2%);
}

.notification-is-unread {
    background: rgba(0,0,0,0.8);
}

.list .list-item {
    box-shadow: 0 1px 0 #4a4a4a;
}

.ui-autocomplete {
    background: hsl(0, 0%, 2%);
    box-shadow: 0 0 0 1px hsl(0,0%,20%);
}

.ui-state-focus {
    background: #111;
}

[type="search"]:focus {
    background-color: #111 !important;
}

.navbar-search {
    background: hsl(0,0%,6%);
}

#upload-container {
    background-color: rgba(0,0,0,0.8);
}
}
