css:
@-moz-document url-prefix("http://www.azlyrics.com/lyrics") {
.noprint, .navbar, .footer-wrap, form, .div-share, .lyricsh, .ringtone, .smt, br {
    display: none !important;
}

body, body * {
    background: #222 !important;
    color: #fff !important;
    font-family: 'Roboto';
    font-weight: 600;
    font-size: 14px;
}

b {
    color: #ddd !important;
    min-height: 40px !important;
    display: block !important;
}

.text-center div br {
 	display: initial !important;   
}

.main-page {
    display: table;
    width: 100%;
    height: 100%;
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    padding: 100px;
    box-sizing: border-box;
}

.row {
    width: 100% !important;
    display: table-cell !important;
    height: auto !important;
    vertical-align: middle;
}

.text-center {
    width: 100%;
}
}
