css:
@-moz-document url-prefix("https://virtonomica.ru/") {
#info2 .money {
 color: #fff !important;   
}
#info2 .points {
 color: #fff !important;
 font-weight: bold;    
}
#info2 .points_button {
 background-color: transparent !important;
 color: #fff !important;;
 font-size: 12px !important;;
 text-decoration: underline;   
}
#info2 .points_button:hover {
 text-decoration: none;
}
}

@-moz-document regexp("https://virtonomica.ru/.*/main/company/view/.*/unit_list/equipment.*") {
.mainHintPanel, .btn-success {
 display:none !important;   
}
}
