css:
@-moz-document url-prefix("http://one.npr.org/") {
header.site__header{
    background-color: black;
}

header.site__header .site__nav__overlay{
	background-color: hsla(0,0%,0%,.7);
}

header.site__header .site__nav__sub-menu li{
    background-color: #272727;    
}

metadata{
 	color: white;
	background-color: rgba(0,0,0,0.8);
}

player{
	background-color: rgba(0,0,0,0.9);
    border: 0px;
    padding: 25px;
    margin: 0px !important;
    height: 162px;   
}

interesting-button>button, interesting-button>a.button, share-button>button, share-button>a.button, read-button>button, read-button>a.button{
 background-color: transparent; 
 color: #E0e0e0;
}
    
story-card .card__slug__wrapper.grid__row>.grid__column, card .card__title, card .card__meta, card .card__meta--light, header.site__header .site__nav__item.site__nav__item--linkless{
 color: #E0e0e0;   
}
    

}
