css:
@namespace url("http://www.w3.org/1999/xhtml");
@-moz-document domain("synthtopia.com") {
  body {
    background-color: #cfc6ad !important;
  }

  #site-navigation, .navigation, .post, .search-field, .footer-widgets, #comments {
    background-color: white !important; 
  }

  .site-branding, .site-info, #main, #secondary, #tertiary {
    background-color: #ebebe2 !important; 
  }

  .site-branding, .site-info {
    margin: 0 auto !important;
    padding: 1em 0 !important;
    width: 1088px !important;
  }

  .site-info {
    margin: 0 auto 1em !important;
  }

  #site-navigation, .footer-widgets {
    margin: 0 auto !important;
    padding: 1em 2em !important;
  }

  .post, .navigation, #comments, #main, #secondary, #tertiary {
    margin: 0 !important;
    padding: 1em !important;
  }

  .entry-title {
    margin-left: 0 !important;
    margin-right: 0 !important;
  }

  #content {
    border-top: 0 !important; 
  }

  #secondary, #tertiary {
    width: 180px !important; 
  }

  #main center, div[id^="div-gpt-ad"] {
    display: none !important;
  }
}
