css:
@-moz-document domain(wikipedia.org) {
  #bodyContent {
    -moz-column-width:512px;
    -moz-column-gap:64px;
  }
  #bodyContent p {
    text-align:justify;
  }
  #bodyContent * {
    max-width: 512px;
    height: auto !important;
  }
  body {
    font-family: "Trebuchet MS", Trebuchet, Arial, sans-serif !important;
  }
}
