css:
@-moz-document domain("elm-lang.org") {
html, body, p, div, h1, h2, h3, h4, h5, #navbar, input {
 color: #DCE1EA !important;
 background-color: #191E29 !important;
}

a {
 color: #6699CC !important;   
}

a:visited {
 color: #C594C5 !important;   
}

pre, code, input, #tabs {
 background-color: #263238 !important;
}

pre, .docs-entry, .footer, input, .pkg-nav, .catalog-sidebar, .pkg-summary, #navbar, hr {
 border-color: #384B54 !important;
}

.hljs-type, code {
  color: #DCE1EA !important; 
}

.hljs-comment {
  color: #585A56 !important; 
}

.markdown-section {
  color: #7b7b7b !important;
}

.entry-list {
 width: 80% !important;
 overflow: auto !important;
}

.center {
 width: auto !important; 
 margin: 0 10% !important;
 display: flex !important;
}
}
