css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("milanos.pl") {
  
  body {
    background: #424140; 
  }
  nav {
    background: #deb887 none repeat scroll 0 0 !important;
  }
  .nav2 {
    background: #35434f none repeat scroll 0 0 !important;
  }
  .actagtop {
    background: #35434f none repeat scroll 0 0 !important;
  }
  element.style {
    background: #35434f none repeat scroll 0 0 !important;
  }
  .w4.post-footer {
    background: #fffff0 none repeat scroll 0 0;
  }
  .green {
    color: green;
    font-weight: bold;
  }
  .red{
       color: red;
    font-weight: bold;
  }
  .tagud > div{
    padding-left: 40px;
  }
  ul#ultag > li > a:link,
  ul#ultag > li > a:visited{
    color: #deb887 !important;
    text-transform:capitalize;
  }
  ul#ultag > li > a:hover{
    color: red !important;
  }
  .tope {
     bottom: -10px;
     position: absolute;
  }
}
