css:
@-moz-document domain("www.edshipyard.com") {
HTML, BODY {
	margin: 0;
	padding: 0;
	background-color: #1c333e;
	color: #abfe84;
	font-family: 'Monospace', 'Geneva', sans-serif;
	font-size: 100%;
}

LABEL {
	display: inline-block;
	position: relative;
	margin: 0;
	border: 0;
	padding: 0;
	white-space: nowrap;
	color: #f3c63a;
}

BUTTON {
	margin: 0;
	cursor: pointer;
	border-color: #f3c63a;
	background-color: #eee;
	padding: 0 0.5em;
}

INPUT:focus,
SELECT:focus,
BUTTON:focus {
	background-color: #f3c63a;
}

OPTION {
	background-color: #f3c63a;
	color: black;
}
OPTION:disabled {
	background-color: #ccc;
	color: #f3c63a;
	font-style: italic;
}

DIV.slotmodule BUTTON.module {
	flex: 1 0 auto;
	margin-right: 0.25em;
	border: 1px solid #182931;
	background-color: #182931;
	color: #f3c63a;
	padding: 0.2em;
	line-height: 1em;
	text-align: left;
}
DIV.slotmodule BUTTON.module:focus {
	outline: none;
	border: 1px solid #f3c63a;
	background-color: #f3c63a;
	color: #f3c63a;
}

DIV.slotmodule BUTTON.modified {
	background-color: #f3c63a;
}

}
