css:
@-moz-document domain("smash.gg") {
.page-section.page-section-grey {
    background-color: #00ffff;
}

.bracket-round-heading h2{
	color: white; 
	font-weight: 400;
}

.bracket-round-heading h4{
	font-weight: bold;
    color: white;
}

.match>.match-affix-wrapper>.match-section, .rr-match>.match-affix-wrapper>.match-section{
	border: none;
}

.match-affix-wrapper .match-label{
	left: -15px;    
}

/*.match-section-top{
 border-bottom: 0px solid white !important;   
}*/
.bracket .match-section:before, .bracket .match-section:after{
    border: 4px solid #e6e6e6;    
}

/*.match-section:after{
	top: -2.5px;
}

.match-section-top{
	top: 22px;
}*/

.bracket .match.to-side .match-section:after, .bracket .to-side.rr-match .match-section:after{
	top: 22px;
}

.match-section-top:before, .match-section-bottom:before{
	left: -15px !important;    
}

.match .match-player.entrant.winner .match-player-info .match-player-stocks, .rr-match .match-player.entrant.winner .match-player-info .match-player-stocks {
    font-weight: 800 !important;
}

.match .match-spacer, .rr-match .match-spacer{
    border: 2px solid #e6e6e6;
}
}
