css:
@-moz-document domain("mail.google.com") {

  .m td {
    line-height: 1.6;
  }

  .lb {
    line-height: 1.87;
  }

  .lb a {
    font-size: 100% !important;
  }

  td[width="120"] {
    width: 160px;
  }

  td[width="120"] .m a,
  td[width="120"] .l a {
    white-space: nowrap;
  }

  .th tr > td:first-child + td {
    width: 120px !important;
    padding-right: 20px;
    overflow-x: hidden;
    text-overflow: ellipsis;
  }

  .th tr > td:first-child + td + td {
    width: auto !important;
  }

  .gb_7a{
    border-radius: 15%;
  }

}

@-moz-document domain("hangouts.google.com") {

  .jr .xU {
    border-radius: 15%;
  }

}
