css:
@-moz-document domain("mail.google.com") {
div.ha {
    position: fixed;
    top: 96px;
    background: white;
    border: 1px solid #d8d8d8;
    background-image: -webkit-linear-gradient(top, #f5f5f5, #f1f1f1);
    z-index: 2;
    width: 63%;
    padding-top: 30px;
    padding-bottom: 10px;
    padding-left: 10px;
    padding-right: 10px;
}
div.nH.if {
    margin-top: 110px;
}
div[role="menu"] {
    z-index: 10;
}
div.ade {
    position: absolute;
    left: 5px;
    top: 10px;
    z-index: 3;
}
}
