css:
@-moz-document domain("journals.aps.org"), domain("physics.aps.org") {
body, section#article-body, div#article-actions.article-nav,
div#t1,
div#t2,
div#t3,
div#t4,
div#t5,
div#t6,
div#t7,
div#t8,
div#t9,  /*only up to 9 tables xD*/
figure,
footer,
#homepage-body
{
background-color:#3c3c3c !important;
color:#CCCCCC !important;
}

li, label{
color:#CCCCCC !important;
}


div, div b, font, p,
h1,h2,h3,h4,h5, h5, h6, h7, h8{
color: #CCCCCC !important;
}



.panel,
div.article.panel.article-result,
.row.panels,
div.panel.header-panel,
section.article,
section.article.open.abstract,
section.article.authors.open,
section.article.authors.open,
section.article.fulltext,
section.article.supplemental,
section#references.article.references,
#slideshow > div.orbit-container > ul,
#slideshow > div.orbit-container > div.orbit-bullets-container,
#slideshow > div.orbit-container,
div#slideshow.reveal-modal.open,
.references,
div.large-12.columns.panel,
div.citing.article.panel,
.disp-formula.panel,
.past-issues
{
background-color:#1c1c1c !important;
color: #CCCCCC !important;
}

._lv_q:hover, ._lv_u:hover, ._lv_t:hover{
background-color:#666666;
}


}
