css:
@-moz-document domain("toggl.com") {
.NavigationContainer__logo {
	width: 25px;    
}

.NavigationContainer__outerBox {
	max-width: 60px;
}

.NavigationContainer__manage,
.NavigationButton__label {
	visibility: hidden;
}

/* drag the flyout navigation back left */
.ReportsMenu__container.ReportsMenu__open {
	left: 40px !important;    
}

.right-pane,
.TimeEntryForm__newTimeEntry {
    max-width: 1200px;
    margin: auto;
}
.body.snowball .right-pane {
	margin-left: auto;    
}

.TimeEntryForm__newTimeEntry {
    left: 0;
}
.snowball #modal-region {
    left: 60px;    
}

/* ...but do display labels for 'extra' items - they don't have icons */
.NavigationContainer__extraItem .NavigationButton__label {
	visibility: visible;
}

/* don't indent the 'extra' items */
.NavigationContainer__extraItem .NavigationButton__icon {
    display: none;
}
}
