@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("marmoset.co") {
  
  body.custom-background, .content-wrap, #sidebar-bg, #sidebar-wrap, #sidebar-right .widget, .brick-meta-wrap, .brick, .pukka-layout-center #left-sidebar-wrap
  {
    background-color : #292929 !important;
  }
  
  a.marmocall, a.marmocall:visited
  {
    background-color : #222 !important;
  }

  #sidebar-top
  {
    background: none !important;
  }
  
  a, a:visited, #main-menu li a:hover
  {
    color : #ff0044 !important;
  }
  
  a.marmocall, a.marmocall:visited, .content-wrap b, .content-wrap strong
  {
    color : #AAA !important;
  }
  
  body, .content-wrap, h1, h2, h3,h4,h5, #main-menu li a, .brick-content h3, .brick-content h3 a
  {
    color: #888 !important;
  }
  
 #social-menu a, #social-menu a:visited, #sidebar, #sidebar a, #sidebar a:visited, #copy
  {
    color : #888 !important;
  }
  
  div.producttitle h1, .product-exc
  {
    color : #FFF !important;
  }
  
  div.brick
  {
    box-shadow: 1px 2px 2px #222 !important; 
  }
   
}
