css:
@-moz-document domain("angular.io") {
/**
 * Angular.io Dark Theme v1.2.0
 * License: https://creativecommons.org/licenses/by-sa/4.0/
 */

 html, body {
 	background: #263238;
 }

 input.st-default-search-input,
 a.nav-title,
 nav.dropdown,
 nav.sidenav,
 div.banner,
 article.l-content-small.grid-fluid.docs-content,
 li.nav-list-item.is-selected {
 	background: #272822;
 }
 
 li.sidenav-section-divider > h3 {
 	color: #272822 !important;
 }

 tbody {
 	background: #565754;
 }

 pre, div.filetree, code {
 	filter: invert(90%);
 }

 p, li, h1, h2, h3, h4, h5, h6,
 a.nav-title,
 li.nav-list-item > a {
 	color: #dfe5e7 !important;
 }
 nav.dropdown > button > span {
 	color: #fff;
 }

 a {
 	color: #5fa6ec;
 }
 a.nav-title.is-parent.is-selected,
 li.nav-list-item.is-selected > a {
 	color: #5fa6ec !important;
 }
}
