css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("shrib.com") {
  body {
    color: #eaeaea !important;
    text-align: center !important;
}
  .zuex {
    background-color: #191919;
}
  .zeige {
    background-color: #191919;
}
  .linggs {
    background-color: #191919;
    width: 350px;
    color: #eaeaea;
    border: 2px solid #191919;
}
  .uffalle, button.uffalle, .linggs a.witerGo {
    background-color: #191919;
    color: #ffffff;
    border: 1px solid #323232;
}
  .uffalle:hover, button.uffalle:hover, .linggs a.witerGo:hover {
    background-color: #323232;
    color: #ffffff;
}
  .linggs .fa:hover {
    color: #eaeaea;
}
  .connect {
    background-color: #323232;
    width: 350px;
    border-top: 1px solid #191919;
    border-right: 2px solid #191919;
}
  .linggs li, .linggs button, .linggs a, .zrugg a {
    background-color: #191919;
    color: #ffffff;
    border: 1px solid #323232;
    box-shadow: 1px 1px 2px 0 #323232;
}
  .linggs li:hover, .linggs button:hover, .linggs a:hover, .zrugg a:hover {
    background-color: #323232;
    color: #ffffff;
}
  .linggs h2 {
    font-size: 18px;
    color: #ffffff;
    border-top: 5px solid #323232;
    text-align: center;
}
  .linggs form {
    background-color: #191919;
}
  p {
    background-color: #191919 !important;
  }
  input#URL {
    background-color: #323232;
    color: #eaeaea;
    padding-left: 5px;
  }
  .intro {
    color: #eaeaea;
    background-color: #191919;
    box-shadow: 3px 6px 15px 0 #aaa;
}
  .umIgob > textarea {
    background-color: #323232;
    color: #ffffff;
}
  .umIgob > textarea, .umIgob > pre {
    background-color: #323232;
    color: #ffffff;
}
  textarea, pre {
    background-color: #323232;
    color: #ffffff;
}
  .rolle {
    background-color: #323232;
    color: #ffffff;
}
}
