css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document regexp('(http|https)://(www|m|magnet|esports)\.(xataka|xatakandroid|xatakamovil|xatakaciencia|xatakaon|genbeta|vidaextra|motorpasion|elblogsalmon|vitonica|applesfera).com/.*') {
body{
        background-image: none !important;
        background-color: #FFF !important;
    }

    
    .ad, 
    .aside,
    .ad-desplegable, 
    .article-aside,
    .article-aside-content,
    .sidebar-seo-links, 
    .closure, 
    .cookies-overlay,
    .nav-blog, 
    .site-logo, 
    .foot,
    .footer,
    .foot-external,
    .userbar,  
    .social-sharing-widgets,
    .article-author-details, 
    .comment-deleted, 
    .comment-toggle,
    .section-related,
    .article-vivephilipstv,
    .related-leaving-item,
    .article-links,
    .article-social-share,
    .m-branded,
    #favicons-list,
    #fb-share-button,
    #div-gpt-top_ad_container,
    .home-video-stream,
    .comment-author-link,
    #twttrHubFrame,
    #featuredContainer,
    .section-native-container,
    #relatedHorizontalPosts,
    .aside-club-content,
    .twttrHubFrame,
    .section-pinned-container,
    .related-item,
    .section-instagram-container{
        display:none !important;
    }
    
    .customize-me{
        margin-top:-50px !important;
    }
    .container,
    .content-fixed{        
        width:80% !important;  
        margin-left: auto !important;
        margin-right: auto !important;
    }
    .content-fixed .container,
    .customize-me,
    .content-dinamic,
    .section-articles,
    .section-articles .article{
       width:100% !important;
    }
    
    #comments{        
        margin-left:-80px !important;        
        width:125% !important;
        
    }
    
    .head{
        position: initial !important;
    }
 
    .comment-list{
        margin:0px !important;
        
    }
    .comment-meta{
        width:150px !important;
        max-height: 24px !important;
    }
    .comment-content{
        margin-left: 0 !important;
        padding-left:3% !important;
        padding-right:5% !important;
        margin-top:25px !important;
    }
    
    .comment-reply-1{
        margin-left:30px !important;
    }
    .comment-reply-2{
        margin-left:60px !important;
    }
    .comment-reply-3{
        margin-left:90px !important;
    }
    .comment-reply-4{
        margin-left:120px !important;
    }
    .comment-reply-5{
        margin-left:150px !important;
    }
        
    .section-comments .comment-author-image, .section-comments .comment-author-name, .section-comments .comment-author-stars, .section-comments .comment-author-link, .section-comments .comment-author-expert{
        margin: 0;
    }
    
    .comment-meta{
        margin-left: 30px;
    }    
    .article-asset-normal{
        width:100% !important;
        max-width: 100% !important;
    }
    .section-recent-list, .home-nav-list, .home-nav-load
    {
       width: 70% !important;
       margin-right: 15% !important;
       margin-left: 15% !important;
    }
    .blob p, .blob ul, .blob ol, .blob li, .blob blockquote{
        max-width:900px !important;
    }
    
    .article-normal-header h1{
        margin-right: 0 !important;
        text-align: center !important;
    }
    .article-asset-video{
        margin-left: 0 !important;
    }
}

@-moz-document domain("xataka.com") {
    .customize-me{
        margin-top:-130px !important;
    }
    .article-classic{
        border: 0 !important;
    }
}
