css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("spritmonitor.de") {
  
  body {
    background-color: #2e2e2e;
  }
  
  #page {
    
  }
  
  #header {
    background-color: #2e2e2e;
    background-image: none;
    border: 1px solid grey;
  }
  
  #navi {
    background-color: #2e2e2e;
    border: 1px solid grey;
    color: white;
  }
  
  #navi a {
    color: white;
  }
  
  #navi #navilinks {
    color: white;
  }
  
  #contentcontainer {
    background-color: #3e3e3e;
    color: white;
  }
  
  #contentcontainer a {
    color: orange;
  }
  
  .fronttitle {
    color: orange;
  }
  
  #sidebar {
  }
  
  .sidebarbox {
    background-color: transparent;
    border: none;
    border-bottom: 1px solid grey;
  }
  
  .sidebarbox h3 {
    color: orange;
  }
  
  #content-left {
    border-right: 1px solid grey;
  }
  
  #content-left h1 {
    color: orange;
  }
  
  .costanalysistable thead tr th {
    color: white;
    border: 1px solid grey;
  }
  
  input[type=text] {
    background-color: #4e4e4e;
    border: 1px solid orange;
    padding: 3px;
    color: white;
    box-shadow: 0px 0px 5px grey;
  }
  
   input[type=text]:active, input[type=text]:focus {
     border: 1px solid red;
     background-color: #E6E6E6;
     color: black;
  }
  
  select, textarea {
    border: 1px solid orange;
    background-color: #4e4e4e;
    padding: 3px;
    color: white;
  }  
  
  input[type=submit] {
    border: 1px solid orange;
    padding: 2px 10px 2px 10px;
    color: white;
    background-color: #2e2e2e;
    box-shadow: 0px 0px 5px grey;
  }
  
    input[type=submit]:hover {
    border: 1px solid red;
    background-color: #4e4e4e;
    box-shadow: 0px 0px 5px orange;
  }
  
  #footer {
    background-color: #2e2e2e;
    color: grey;
  }
  
  #footer a {
    color: orange;
  }
  
  /*Detailpage*/
  
  .detailtable tr {
    background-color: grey;
  }
  
  .spacer, .costgroup {
    background-color: #2e2e2e !important;
  }
  
  .costdate, .costkmpos, .costname, .costprice {
    color: white !important;
  }
  
  .btn-klappbox {
    background-color: transparent;
    box-shadow: 0px 0px 3px orange;
  }
  
  
}
