css:
@-moz-document domain("koalabeast.com") {
body {
	background: #000;
}

h2.header-title {
	color: #FFF;   
}



.homepage-layout {
	background-color: black;
}

#nav-tagpro {
	color: #FFF;
}

/* Shrink and center logo */

#logo img {
    position: absolute;
    top:0;
    right: auto;
    left: auto;
    text-align: center;
    width: 500px;
}

#profile-btn {
	display: block !important;
	background: #FFF;
}

/* Top tabs */
#site-nav > ul li a {
	color: #FFF;
    border: 2px solid #777;
    border-bottom: 0px solid #000
}

/* Active Tab */
#site-nav > ul li.active-tab a  {
	border: 2px solid #DDD;
    border-bottom: 0px solid #000
}

div#header.header {
	background-color:black;
    border-bottom: #FFFF00;
}
 /* Changes lines under titles to a brighter color */
.header-title {
	border-bottom-color: #555;
}

#player_uid_893909420_1,
#player_uid_893909420_1 > div.ytp-thumbnail-overlay.ytp-cued-thumbnail-overlay {
	display: none;
}

body > div.container.home > div > div > div.row.main-block > div:nth-child(1){
	width: 100%;
    height: 100% !important;
}



#play-now {
	width: 100%;
    height: 100%;
}

#play-now span.sub-text {
	display: none !important;
}

body > div.container.home > div > div > div > div.col-xs-12.col-sm-8 {
	display:none;   
}

body > div.container.home > div > div > div > div.col-xs-12.col-sm-4 {
	width: 100%;
    left: auto;
    right: auto;
    text-align: center;
    margin-left: auto;
    margin-right: auto;
}

.btn {
	background: #FFFFFF;
    color:#000077 !important;
    box-shadow:none;
    font-size: 24px !important;
}

.btn:hover, .btn:focus, .btn:active {
    background: #AAAAAA;
}

.footer {
	background-color: #000;
}

footer.footer .header-title,
footer.footer a {
	color: #FFF !important;   
}

#game-controls {
	display:none;
}

body > div.container.home > div > div > div.row.main-block > div.col-md-6.text-center {
	display:none;   
}
}
