css:
@-moz-document url-prefix("http://www.joelonsoftware.com/articles"), url-prefix("https://www.joelonsoftware.com/articles"), url-prefix("http://www.joelonsoftware.com/items"), url-prefix("https://www.joelonsoftware.com/items") {
body > div,
body > div > div > div,
body > div > div > div {
	margin: 0px auto;
	width: 100% !important;
	max-width: 900px !important;
}
body > div > div > div > div {
	width: 100% !important;
	max-width: 740px !important;
}
body > div > div > div > div > p {
	font-family: 'Merriweather', Georgia, 'Times New Roman', Times, serif;
	-moz-osx-font-smoothing: grayscale;
	-webkit-font-smoothing: antialiased !important;
	-moz-font-smoothing: antialiased !important;
	text-rendering: optimizelegibility !important;
	text-align: justify;
	text-justify: inter-word;
	word-spacing: .2em;
	font-size: 1.3em;
	font-weight: 500;
	line-height: 1.5em;
}
body > div > div > div > div > p > img {
	padding: 5px 10px !important;
}
p > span.side {
	position: inherit;
	margin-left: 0px;
}
}
