css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.freecycle.org") {
  body {
    Background-color: #1D1D1D;
  }
  #main_logo {
    background-color: #FFF;
    padding: 8px;
    -webkit-border-radius: 16px;
    -moz-border-radius: 16px;
    border-radius: 16px;
    background: #fff none repeat scroll 0 0;
    border: 2px solid #b30;
    margin-bottom: 1em;
    padding: 1em;
    box-shadow: 4px 4px 2px #fc8;
  }
  #main_menu {
    border: 2px solid #b30;
    box-shadow: 4px 4px 2px #fc8;
  }
  #main_footer {
    border-top: none;
  }
  #region_column_1 ul li, 
  #region_column_1 ul li a, 
  #region_column_2 ul li, 
  #region_column_2 ul li a, 
  #group_column_1 ul li,
  #group_column_1 ul li a, 
  #group_column_2 ul li,
  #group_column_2 ul li a,
  #group_column_3 ul li,
  #group_column_3 ul li a {
    color: #FFF;
  }
  #donatePage {
    background-color: #FFF;
    border: 2px solid #b30;
    box-shadow: 4px 4px 2px #fc8;
  }
  section p {
    background-color: #FFF;
    border: 2px solid #b30;
    box-shadow: 4px 4px 2px #fc8;
    -webkit-border-radius: 16px;
    -moz-border-radius: 16px;
    border-radius: 8px;
    padding: 1em;
  }
  section header h1,
  section header h2 {
    color: #FFF;
  }
  #main_footer a {
    color: #FFF;
  }
  #region_column_1 ul li a, 
  #region_column_2 ul li a, 
  #country_column_1 ul li a, 
  #country_column_2 ul li a,
  #country_column_3 ul li a {
    color: #FFF;
  }
  #blocker_message {
    border: 2px solid #b30;
    box-shadow: 4px 4px 2px #fc8;
    padding: 1em;
  }
  section article ul, section article ul li a {
    color: #FFF;
    list-style: none;
  }
}
