css:
@-moz-document domain("fritz.box") {
html {
background-color: #f9f9f9;
}

#userMenuBox, #menuBox {
    background-color: #f9f9f9;
}


header {
background-color: #444444;
}

.logoBox {
    background-color: #444444;
}

.breadcrump {
    background-color: #696969;
}

.submenu.open .menu_item.selected, .menu_item.selected {
    background-color: #444444;
}

.row .header .title {
    background-color: #696969;
}

.row .header {
    border-bottom: solid 2px #696969;
}

.tab_menu.show {
    background-color: #fff;
}

.tab_menu a.show, .tab_menu div.tab.show {
    background-color: #696969;
}

.tab_menu a.show.selected, .tab_menu div.show.selected {
    border-bottom: none;
    height: 2.5rem;
    color: rgb(248, 236, 23);
}

#btn_form_foot button, div.btn_form_foot button, .bottomMenu div {
    background-color: #696969;
}
}
