*/

@-moz-document domain(online.sbis.ru) {
   .shockedButton .icon-EmoiconShocked:before,
   .shockedButton .icon-EmoiconShockedInvert:before,
   .shockedButton:hover .icon-EmoiconShocked:before,
   .shockedButton:hover .icon-EmoiconShockedInvert:before {
    	color: #ccc!important;
	}

   .shocked .icon-EmoiconShocked:before,
   .shocked .icon-EmoiconShockedInvert:before,
   .shocked:hover .icon-EmoiconShocked:before,
   .shocked:hover .icon-EmoiconShockedInvert:before {
      color: #ccc!important;
   }
  
   .icon-16.icon-EmoiconShocked.icon-hover,
   .icon-16.icon-EmoiconShockedInvert.icon-hover,
   .icon-16.icon-EmoiconShocked.icon-hover.shockedButton.emoji-rate-hover,
   .icon-16.icon-EmoiconShockedInvert.icon-hover.shockedButton.emoji-rate-hover
