css:
@-moz-document domain("kotaku.com"), domain("io9.com"), domain("deadspin.com"), domain("gawker.com"), domain("gizmodo.com"), domain("jalopnik.com"), domain("jezebel.com"), domain("lifehacker.com"), domain("kinja.com"), domain("gawker-labs.com") {
.global-nav {
    position: fixed !important;
}
.topnav__logostrip .logo img,
.topnav__logostrip .logo svg {
    max-height: 30px !important;
}
.topnav__logostrip,
.topnav__logostrip.topnav__logostrip--subtitle .topnav__logo-wrapper {
    height: 70px !important;
}
.js_newsletter-btn--subscribe,
.streamshare,
.sidebar,
.recirculation-unit .sidebar-module {
    display: none !important;
}
@media only screen and (min-width: 50em) {
    .navwrap--outer.navwrap--frontpage, .navwrap--outer.navwrap--frontpage .global-nav {
        height: 140px !important;
    }
    .navwrap--outer .global-nav.global-nav--scrollback.fixed {
        display: none !important;
    }
}
@media only screen and (min-width: 64em) {
    .pe_newlayout .main {
        border-left: none !important;
        margin: 0 auto;
    }
}
}
