css:
@namespace url("http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul");

/*Selected tabs text color normal and private*/
.tabbrowser-tab[selected="true"],.tabbrowser-tab:hover[selected="true"] {
  color: rgb(0,173,238) !important;
  text-shadow: 0px 0px 5px rgba(0,173,238,.75) !important;
  font-weight: normal !important;
  }
[privatebrowsingmode=temporary] .tabbrowser-tab[selected="true"]{
  color: rgba(196,111,224,1) !important;
  text-shadow: 0px 0px 5px rgba(196,111,224,0.75) !important;
  font-weight: normal !important;
  }
