css:
@-moz-document domain("www.scirra.com") {
body {
    background: #222222;
}
.content-wrap p,
h1,
h2,
h3,
h4,
h5,
.content-wrap a {
    color: white !important;
}
.sub-menu li.selected {
    background: #313131 !important;
}
a.selected {
    background: #313131;
}
.ManualSearchBox,
.ch {
    color: black !important;
}
td,
.rep,
span,
em,
strong,
.pagination,
dd,
label,
.notice,
.homepage-icon-big,
ul li,
.content,
a,
a:visited,
h2,
.signature,
p {
    color: white !important;
}
.post,
.postview,
h3,
.left-menu,
.inner-list-content,
.list-view-wrap,
.list-view-audio-preview,
.jplayerWrapper,
.content,
.clear,
.row,
.topic-list,
table,
.newforum-tbl-wrap,
thead,
tr {
    background: #414141 !important;
}
dd .profile-icons {
    background: #222 !important;
}
blockquote {
    background: #323232 !important;
}
.row {
    border-bottom: #121212 !important;
    border-top-color: #121212 !important;
}
.c3Inner,
.c3SecBreak,
.c3Content {
    background-color: #323232 !important;
}
.user-dropdown {
    background: #323232;
}
.top-tear,
.bottom-tear,
.footer-tear {
    background-image: none;
}
.ar {
    color: white !important;
}
.content-wrap h3,
.content-wrap table,
.content-wrap td {
    background-color: #232222 !important;
}
.cp-mini {
    background-color: #232222;
}
}
