*/
.dictionary-header, .thesaurus-header {
    position: relative !important;
    top: 0px;
}
.right-rail-sticky-box sticky-box,
#fly-out {
    display: none;
}
iframe,
#google_image_div,
#google_image_div * {
    display: none !important;
    top: -1000px !important;
    height: 1000px;
}
div.survey,
div.left-nav-sticky {
    display: none !important;
}
}
