css:
@-moz-document domain("twitch.tv") {
/* 
 * Name:    Modify whisper window on Twitch
 * Title:   Make Twitch great again with this simple Stylish script
 * Author:  XxBonf1r3xX
 * Version: 3.0
 *
 */

.conversation-window {
    height: 560px;
    bottom: 520px;
}

.conversation-window .conversation-content {
    height: 410px;
}

.conversations-list {
    height: 440px;
    bottom: 50px;
    max-height: inherit;
}

.conversations-list .conversations-list-inner {
    max-height: 425px;
}

.conversations-list .scroll-container {
    height: 425px;
}

.conversation-window .conversation-input-bar .chat-input textarea {
    padding-right: 23px;
    height: 100px;
}

input, textarea, select {
    border-radius: 0px;
}

.conversation-window.has-unread .convoHeader {
    background-color: #3168cb;
}
}
