css:
@-moz-document url(http://www.dumpert.nl/), domain(dumpert.nl) {

#commentscontainer {
 display:none !important;
}
body {
  background-color:#000 !important;
  background-image:none !important;
}
body.overview .dump-cnt .pagecontainer {
  margin: 10px 0 20px 0 !important;
}
.contentselector {
  display:none !important;
}
.dump-cnt {
  background:#fff !important;
}
.dump-cnt>article {
  background:none !important;
  border:none !important;
}
.dump-pan {
 background-color:#fff !important;
}
.dump-side {
  background-color:#000;
}
.header {
  background-image:none !important;
}
#dumphole {
  display:none !important;
  background:#000 !important;
}
#top5 {
  margin-top:125px !important;
}
#top5 .dumpthumb {
  border-bottom:none !important;
}
#top5>h1 {
  font-family: "Helvetica Neue Black", Arial, sans-serif !important;
  font-size:21px !important;
}
#top5 a.dumpthumb {
  border:none !important;
}
a.dumpthumb {
  border-top: 1px solid #e6e6e6 !important;
  border-right: 1px solid #fff !important;
  border-bottom: 1px solid #fff !important;
  border-left: 1px solid #fff !important;
  background:none !important;
}
a.dumpthumb:hover {
  border-top: 1px solid #66c221 !important;
  border-right: 1px solid #fff !important;
  border-bottom: 1px solid #fff !important;
  border-left: 1px solid #fff !important;
}
.dump-ftr {
  display:none !important;
}
#upcoming, #dkheaders, #gsheaders, #autobahn {
  display:none !important;
}
.dump-soc {
  display:none !important;
}
.dump-rate .dump-kudos, .dump-rate .dump-views>div, .dump-rate .dump-reactions>div {
  border-radius:0px !important;
  padding: 12px 0 12px 0 !important;
  border-top: 1px solid #e6e6e6 !important;
  border-left:none !important;
  border-right:none !important;
  border-bottom:none !important;
}
.dump-reactions {
  text-align:left !important;
}
.dump-kudos .dump-mut {
  right:0 !important;
}
.pagination li {
  font-weight: bold !important;
}
.pagination li.volgende a span, .pagination li.vorige a span {
  display:none !important;
}

}
