css:
@-moz-document domain("onlinepizza.se") {
.items.sponsored,
.items.closed,
.items.takeout,
.background,
.liveChatStatic,
footer {
    display: none !important;
}
body {
    background: #cccccc;
}
.page-restaurants .overview,
.overview,
.products,
.items header,
aside section {
    border-bottom: 1px solid #999999;
    margin-top: 5px;
    margin-bottom: 5px;
    background: #ffffff !important;
    box-shadow: 2px 3px 4px #999999;
    border-radius: 10px;
}
.items header {
    margin-top: 50px;
}
div[role='main'] {
    float: left;
    width: 100%;
    max-width: 650px;
    margin-bottom: 80px;
    -webkit-box-shadow: none;
    -moz-box-shadow: none;
    box-shadow: none;
}
.page-restaurants .delivery ul li {
    height: 90px;
    border-bottom: 1px solid #999999;
    margin-top: 5px;
    margin-bottom: 5px;
    background: #ffffff !important;
    box-shadow: 2px 3px 4px #999999;
}
section {
    background: none !important;
}
.page-menu .items {
    border: none !important;
}
.products .menuRow {
    padding: 5px 5px 5px 5px !important;
    margin: 0px 5px 0px 5px;
    border-bottom: 1px solid #c9c9c9;
}
.page-restaurants .delivery,
.page-restaurants .takeout {
    border: 0px;
}
}
