css:
@-moz-document domain("chan.sankakucomplexxx.com") {
#post-content {
    display: flex;
    justify-content: center;
    max-height: 100%;
    max-width: 100%;
    padding-top: 0px!important;
    width: 100%;
}
video#image {
    max-height: 900px;
}
#post-content img, #image {

    position: relative;
    max-width: 100%;
	margin-left: auto;
	margin-right: auto;
}
#post-content img {
 height: auto;   
}

body,
body.en,
body.ja,
#header,
div#header,
div#header ul#subnavbar, #advanced_search, div.status-notice, div.status-notice.pending
 {
    background: #212121;
}
#add-to-favs, #remove-from-favs {
    background: none;
}
#headerlogo {
    display: none;
}
#popular-preview {
    border: 0px;
    background: none;
}
a:link,
a:visited {
    color: #BDBDBD;
}
h5,
h6,
body {
    color: white;
}
.unit-rating li a:hover {
    background: url(https://aww.moe/hwkdqs.png) left bottom;
    z-index: 2;
    left: 0px;
}
.unit-rating {
    background: url(https://aww.moe/hwkdqs.png) left center repeat-x;
}
.unit-rating li {
    background: url(https://aww.moe/hwkdqs.png) left top;
}
#advanced_search {
	 margin-bottom: 0;
}

#recommended {
 	margin-left: auto;
    margin-right: auto;
}
}
