css:
@-moz-document domain("core.telegram.org") {
/*!  Dark Core.Telegram.org */
/*
 * License: https://creativecommons.org/licenses/by-sa/4.0/
 */
/* AGENT_SHEET */

body {
    background-color: #222 !important;
    background-image: url(data:image/png;base64,) !important;
    background-clip: border-box !important;
    background-origin: padding-box !important;
    background-attachment: fixed !important;
    background-repeat: repeat !important;
    background-size: auto !important;
    background-position: left top !important;
    color: #ccc;
    font-weight: 100 !important;
}
/* custom code font */

pre,
code,
tt,
kbd:not(.badmono),
pre,
samp,
.blob-code,
.file-data pre,
.line-data,
#gist-form .file .input textarea,
.blob-code-inner {
    font-family: "Menlo", Consolas, "Liberation Mono", Menlo, Courier, monospace !important;
}
/* dev_page  */

.dev_page {
    background-color: #151515;
    min-height: 500px;
    color: #a9a9a9;
}

h4 {
    color: #ffffff;
    font-weight: 100;
}
.tl_page_head {
    background-color: rgb(2, 2,2);
}
#dev_page_title {
    color: #fafafa;
    font-size: 2em;
}
.dev_page_nav_wrap > p > a:first-child {
    color: rgba(255, 255, 255, 0.75);
}
.tl_main_wrap h3 {
    color: #969696;
    font-size: 23px;
    font-weight: 500;
}
}
