css:
@-moz-document domain("tjournal.ru") {
.b-sidebar {
  display: none;
}

.b-sidebar+.b-content {
  margin: 0;
  padding: 0;
}

.b-article article .wrapper-wide, 
.b-article article .img_labeled img, .b-article article .wrapper-label img,
body.m-content-wide .b-article article .wrapper-wide {
  width: 100%;
  margin: 0 auto;
}


.b-articles__b_t2 .b-articles__b__picture, .b-articles__b_t2 .b-articles__b__video,
.b-articles__b_t2 .b-articles__b__picture a img, .b-articles__b_t2 .b-articles__b__video a img {
  max-width: 100%;
}

.b-article article .wrapper-image,
.b-article article .wrapper-video {
  margin: 1em -30px;
}

.b-article article .wrapper-image span,
.b-article article .wrapper-video span {
  margin-left: 30px;
}
}
