css:
@-moz-document domain("calendar.synology.com") {
body,
#app-calendar,
.syno_cal .ext-cal-monthview .ext-cal-hd-ct .ext-cal-hd-days-tbl {
    color: white;
	background-color: #272822;
}

.syno_cal .synocal_global_setting,
.syno_cal .synocal_nav {
    color: white;
    background-color: #222;
    border-color: #111;
}

.syno_cal .ext-cal-monthview .ext-cal-bg-tbl, 
.syno_cal .ext-cal-day-header .ext-cal-bg-tbl {
	overflow: hidden;
}

.syno_cal .ext-cal-dayview .ext-cal-body-ct .ext-cal-day-col.day-first,
.syno_cal .ext-cal-dayview .ext-cal-body-ct .ext-cal-day-col,
.syno_cal .ext-cal-dayview .ext-cal-day-header td.ext-cal-dtitle,
.syno_cal .ext-cal-dayview .ext-cal-bg-row-div,
.syno_cal .ext-cal-day,
.syno_cal .ext-cal-day.ext-cal-day-first,
.syno_cal .ext-cal-day.ext-cal-day {
    border-color: #444;
}

.syno_cal .ext-cal-day,
.syno_cal .ext-cal-day.ext-cal-day-first,
.syno_cal .ext-cal-day.ext-cal-day {
	background-color: #272822;
}

.syno_cal .ext-cal-dayview .day_header_wrap:after,
.syno_cal .ext-cal-dayview .day_header_wrap::after,
.syno_cal .ext-cal-dayview .day_body_wrap:before, 
.syno_cal .ext-cal-dayview .day_body_wrap::before,
.syno_cal .ext-cal-dayview .day_body_wrap:after, 
.syno_cal .ext-cal-dayview .day_body_wrap::after {
    background-color: #444;
}

.syno_cal .ext-cal-dayview .ext-cal-body-ct .ext-cal-day-col .ext-cal-evt,
.syno_cal .ext-cal-dayview .ext-cal-body-ct .ext-cal-day-col .ext-cal-evt.dragged,
.syno_cal .ext-cal-dayview .ext-cal-body-ct .ext-cal-day-col .ext-cal-evt.passed,
.syno_cal .ext-cal-ev .ext-cal-evt.passed, .syno_cal .ext-cal-ev .ext-cal-evt,
.syno_cal .ext-cal-ev .ext-cal-evt.passed, .syno_cal .ext-cal-ev .ext-cal-evt.dragged,
.syno_cal .ext-cal-ev .ext-cal-evt.passed, .syno_cal .ext-cal-ev .ext-cal-evt.passed {
    color: white;
}

.syno_cal .synocal_nav .header .title {
    color: white;
}

.syno_cal #app-calendar-week .evt_title, 
.syno_cal #app-calendar-month .evt_title, 
.syno_cal #app-calendar-day .evt_title, 
.syno_cal .evt_tooltip_wrap .evt_title {
	color: white;
}

.syno_cal .synocal_nav .header.with_border {
    border-color: #333;
}
.syno_cal .synocal_nav .divider {
    background-color: #333;
}

.syno_cal .synocal_nav .bottom_line {
    background-color: #222;
    border-color: #333;
}

.syno_cal .syno-ux-datefield-menu .x-date-picker table.x-date-inner tbody tr td.in_view_bound:first-child {
    border-top-left-radius: 3px;
    border-bottom-left-radius: 3px;
}
.syno_cal .syno-ux-datefield-menu .x-date-picker table.x-date-inner tbody tr td.in_view_bound:last-child {
    border-top-right-radius: 3px;
    border-bottom-right-radius: 3px;
}

.todo_wrap .todo_text,
.todo_wrap .todo_text a {
	color: #CCC;
}
}
