@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain('wap.tgfcer.com'), domain('wap.tgfcer.net') {
    /* Write your Css here. */
    div.wrap{
        /*font-size: 14px  !important;*/
        /*background-color: white !important;*/
        max-width:none !important;
    }
    
    span.title:hover{
        background-color: yellow !important;
        text-decoration: underline !important;
    }
    

    span.title a{
        text-decoration: underline !important;    
    }

    span.title, span.paging,span.author{
/*         margin:0  !important; */
    }

    /*span.paging + br,span.title + br*/
    div.dTitle > br {
        display:none !important;
    }
    div.dTitle > span.author {
/*         margin:0 !important; */
        float:right !important;
    }
    div.dTitle {
/*         margin:0 !important; */
        cursor:unset !important;
        clear:both;
    }
    div.infobar{
/*         margin: 0  !important; */

    }
    div.quote{
        color:gray;
    }
    div.message{
/*         margin: 0  !important;  */
        line-height:1.2em !important;
    }
    div#wrapper div{
/*         margin:0 !important; */
    }
    a:hover{
        background-color: yellow !important;    
    }
    a:visited{
/*        color: gray !important;*/
    }
    input{
        border :1px solid black !important;
    }
}
