css:
@-moz-document domain("nobelium.xyz") {
.navbar-brand img {
    content: url("http://i.imgur.com/196Y7M2.png");
}
.panel-primary>.panel-heading {
    background-color: #008000;
}
.navbar,
.navbar-default {
    background-color: #008000;
    border-color: transparent;
}
.panel-heading {
    padding: 10px 15px;
    border-bottom: 0px solid transparent;
    border-top-left-radius: 0;
    border-top-right-radius: 0;
}
body {
    font-family: 'Arial', Arial;
    -webkit-font-smoothing: antialiased;
    background: #008000;
    -moz-background-size: cover;
    background-size: cover;
    -o-background-size: cover;
    -webkit-background-size: cover;
    background-image: url(https://cdn-images-1.medium.com/max/800/1*CNwBG_xcHDvIZg2y-G2yjQ.jpeg)
}
/*-----NAVBAR-----*/

.navbar-collapse.collapse 
.navbar,
.navbar-default {
    background-color: #008000;
}
.navbar .navbar-nav>li>a:hover {
    color: white;
    padding-top: 20px;
    padding-bottom: 20px;
    font-family: Arial;
    font-weight: normal;
    font-size: 1.25em;
    text-decoration: underline;
}
.navbar .navbar-nav>li>a {
    color: white;
    padding-top: 20px;
    padding-bottom: 20px;
    font-family: Arial;
    font-weight: normal;
    font-size: 1.25em;
}
.navbar .navbar-brand {
    height: 60px;
    line-height: 30px;
    font-family: Arial;
    color: #008000;
}
span.panel-panel-primary > .panel-heading-center {
    display: none;
}
}
