css:
@-moz-document domain("dizimag.co"), domain("dizimag1.co") {
/* all domain */

body {
    background: #333 !important;
}
/* BG color */

body > div:nth-child(4) > div:nth-child(1) {
    display: none !important;
}
/* bg click remove */

#header_b > div:nth-child(5) > div > iframe {
    display: none !important;
}
/* top right mini banner */

td > iframe#rk300x250x {
    display: none !important;
}
/* lower right mini banner */

#footer-rek {
    display: none !important;
}
/* lower footer banner */

#backkapat {
    display: none !important;
}
/* remove ads button */
}

@-moz-document url("http://dizimag.co/"), url("http://dizimag1.co/") {
/* mainpage only */

a ~ div:not(#yenill):not(#clearfix) {
    display: none !important;
}
/* mainpage top banner */

td > iframe {
    display: none !important;
}
/* mainpage lower table ad */

td > div#fil {
    width: 860px !important;
}
/* mainpage lower table fill re-width */
}

@-moz-document url-prefix("http://dizimag.co/"), url-prefix("http://dizimag1.co/") {
/* sub pages */

br ~.headblock:not(#goster_bolumliste) {
    display: none !important;
}
/* video page comments - might show spoilers */

.orta > .orta_2 > a ~ div[style]:not(#movie):not(#yenill) {
    display: none !important;
}
/* video page double banner */

#movie #links_extins~table td >div:first-child > div:first-child {
    display: none !important;
}
/* video page player top ad */

#movie #links_extins~table .dizi-sag {
    display: none !important;
}
/* video page player right ads */

#movie #links_extins~table td > center {
    display: none !important;
}
/* video page player lower ad */

#movie #links_extins~table td > a {
    display: none !important;
}
/* video page player lower learn english ad */

#likeornot {
    display: none !important;
}
/* video page like or not */

#mediaspace {
    height: 500px !important;
    position: relative !important;
    z-index: 2 !important;
    width: 860px !important;
}
/* video page bigger player */
}
