css:
@-moz-document url(https://alphareign.se/) {
body {
    background: #212121;
    font-family: "Droid Sans";
	font-size: 14px;
	font-style: normal;
	font-variant: normal;
	font-weight: 400;
	line-height: 22.4px;
}
 a {
    color: #f4f4f4;
    text-decoration: none;
}

.block {
    background-color: #212121;
}

.navbar-default {
    background-color: #f4f4f4;
}

.navbar-padded {
    padding-top: 10px;
    padding-bottom: 10px;
}

.nav-bordered>li>a {
    font-weight: 300;
    color: #f4f4f4;
}



.btn-success {
    color: #fff;
    background-color: #77A577;
    border-color: #77A577;
}

.btn-danger {
    color: #fff;
    background-color: #902340;
    border-color: #902340;
}

.btn-info {
    color: #fff;
    background-color: #80B2C1;
    border-color: #80B2C1;
}

.btn-warning.active, .btn-warning:active, .btn-warning:hover, .open>.dropdown-toggle.btn-warning {
    color: #fff;
    background-color: #D3B182;
    border-color: #D3B182;
}

.label-success {
    background-color: #77A577;
}
.label-success {
    background-color: #77A577;
}

.label-danger {
    background-color: #902340;
}



body, mark {
    color: #f4f4f4;
}

.list-group-item {
    color: #212121;
}
}
