/* Mattermost 24h
 *
 * Make a the timestamp column just wide enough for a 24-hour clock.
 * For best results, enable the 24-hour clock
 * ( Mattermost: Account Settings > Display > Clock Display )
 * Requires the "Mattermost kompact" style sheet.
 *
 * kkeys@caida.org
 */

@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document regexp("https://mattermost\\.[^/]*/[^/]*/channels/.*") {

  @media screen {
    body { /* more specific than ":root" in the "Mattermost kompact" style */
      --time-width: 52px;
    }
  }

}
