css:
@namespace url(http://www.w3.org/1999/xhtml);


@-moz-document regexp("http://www\\.powerapp\\.com\\.tr/.*") {
  body {
    background-image: none !important; 
    background: black !important;
    color: #BCBCBC !important;
  }

  html > body {
    background-image: none !important; 
    background: black !important;
    color: #BCBCBC !important;
  }
  #detailPagesContainer, .box
  {
    background-color: #202020 !important;
    color: #BCBCBC !important;
  }
  .radioDesc, .advertising {display:none}
  .channelLogo > img {
    background-color: #505050;
    border-radius: 5px;
  }
  .info > .artistSongTitle { color: white !important; }
  #footer > .top { background-color:#28292A; }
  #footer > .bottom { background-color:#2D2E2F; }
}
