css:
@-moz-document url-prefix("http://www.eldarya.com.br/"), url-prefix("http://eldarya.com.br"), url-prefix("http://www.eldarya.ru/"), url-prefix("http://www.eldarya.com/"), url-prefix("http://www.eldarya.com.br/"), url-prefix("http://www.eldarya.de/"), url-prefix("http://www.eldarya.es/"), url-prefix("http://www.eldarya.fi/"), url-prefix("http://www.eldarya.hu/"), url-prefix("http://www.eldarya.it/"), url-prefix("http://www.eldarya.pl/") {
#pet-portrait {
background : url("http://imageshack.com/a/img921/2702/L50ic3.jpg") center no-repeat !important ;
background-size : auto 450px !important ;
height : 450px !important ;
}
#pet-portrait > img[src*="/static/img/pet/icon/"] {display : none !important ;
}

#pet-headerPortrait #pet-headerPortrait-base {
background: url("http://imageshack.com/a/img923/1840/DOVZzX.png") no-repeat !important;
-webkit-filter: hue-rotate(0deg);
filter: hue-rotate(0deg);
}

#pet-info-food > img {
background : url("https://media.giphy.com/media/5FSOoHCt1aBq0/giphy.gif") !important;
font-size : 0 !important ; 
background-size : 60px 60px !important ;
margin-top : -5px !important ;
padding-left : 60px !important ;
padding-top : 60px !important ;
width : 0 !important ;
height : 0 !important ;
}
#pet-info-food:before {
content : "Pennyroyal Tea" !important ;
font-size : 20px !important ;
}
#pet-info-food > div {
line-height: 0px;
font-size: 0px;
margin: 13px 0 0 100px;
}
#pet-info-racegender:before {
content : "grunge slave boy" !important ;
font-size : 14px !important ;
line-height : 20px !important ;
}
#pet-info-racegender {
font-size : 0 !important ;
margin-top : 5px ;
}
#pet-info-name:before {
content : "Kurt Cobain" !important ;
font-size : 20px !important ;
}

#pet-info-name {
font-size : 0 !important ;
}
}
