css:
@-moz-document domain("www.scientificamerican.com"), domain("blogs.scientificamerican.com") {
body:not(.forced-mobile) .primary-nav__list__item>a {
    color: #f5f5f5;
}
.hr-titles>span {
    background: #151515;
}
body {
    color: #f5f5f5;
    background: #151515;
}
.sticky-header__inner {
    background: #252525;
    border-bottom: 1px solid #000;
    border-top: 1px solid #000;
}
.sticky-header__inner.is-sticky {
    border-bottom: 1px solid #000;
}
.icon__logo--lg,
.header__logo__icon {
    background-image: url(https://www.scientificamerican.com/mediakit/cache/file/78125CD3-3566-409F-B82A77EF85ACD8E0.png);
}
a.header__right__subscribe,
.header__left__subscribe-btn {
    color: #555;
    border: 2px solid #555;
}
.primary-nav,
.landing-main {
    border-top: 1px solid #000;
}
.tout-feature.special-report .tout-feature__title,
.brunel-deck-semibold-normal-normal-loaded .education-landing-poster__title__inner {
    color: #444;
}
.t_body,
.article-text>p,
.article-text>ol,
.article-text>ul,
.article-text>div,
.player-audio .player-controls input+label,
.player-audio .player-controls button,
.player-audio .player-controls .inverted:checked+label,
.player-audio .player-controls .player-time,
.store-listing__category,
.education-landing-item__main__featured .tx_caption {
    color: #e5e5e5;
}
.pagination__prev:hover,
.pagination__next:hover,
.pagination__nums__item a:hover,
.btn--small:hover,
.hero-tout__cta:hover,
.store-listing__cta:hover,
.btn:hover,
body:not(.forced-mobile) .primary-nav__list__item>a:hover:after {
    color: #e5e5e5;
    border-color: #f5f5f5;
}
.transcript__inner p {
    color: #b5b5b5;
}
.share-box__inner__item {
    background: #eee;
}
.podcasts-listing__control .icon {
    background-image: url(http://www.prakashellenhorn.com/wp-content/uploads/play_button.png);
}
.icon__download--black {
    background-image: url(http://bioinfo.ict.ac.cn/OpenMS-Simulator/images/down-arrow.png);
    width: 18px;
    height: 31px;
}
.icon__search--black {
    background-image: url(http://www.clker.com/cliparts/n/U/H/1/H/u/search-icon-white-one-hi.png);
}
.store-listing,
.education-landing-item__main,
.listing-feature__inner {
    border-color: #444;
}
.store-listing__price {
    font-size: 18px;
}
.education-landing-item__main__featured {
    border-top: 1px dotted #444;
    border-bottom: 1px dotted #444;
}
.btn {
    border-color: #aaa;
}
}
