css:
@-moz-document regexp("https://doc.rust-lang.org/reference.*") {
#TOC{
	position: fixed;
    left: 0;
    top: 0;
    overflow: scroll;
    overflow-x: hidden;
    height: 100vh;
    max-width: 25vw;
    
    border-right: 1px solid #ccc;
    padding-left: 10px;
    padding-bottom: 20px;
    
    background: #2d3143;
}
#TOC a{
    color: #c1c6d7;
}

/* other */
body{
    background: #1C1F2B;
    color: #bdcadb;
    font-size: 1.1em;
    margin-right: 10vw;
    margin-left: 30vw;
    max-width: 70vw;
}
em{
    font-weight: bold;
    border-radius: 4px;
    color: #fff;
}
pre {
    border: 1px dashed black;
    border-radius: 4px;
    background: #2d3143;
}
#versioninfo{
    display: none;
}
code{
    color: #9dbed8;
    background: #2d3143;
    border-radius: 2px;
}

a{
    color: #3eb1d0;
}
body .section-header>a
{
    color: #fffffa !important;
}
}
