css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("learncpp.com") {
  #container {
    background-color: black !important;
  }

  #middle {
    background-color: #202020 !important;
    color: #fff;
  }

  p {
      color:rgb(222, 222, 222);
  }

  .widget, div.text-widget, .widget li {
    background-color: #404040 !important;
    color: #fff !important;
  }

  ul.commentlist li.thread-even, ul.commentlist li.thread-odd {
    background-color: black;
  }

  ul.commentlist div.bypostauthor {
    background-color: #404040;
  }

  div.widget ul, div.textwidget, table {
    background-color: #202020;
  }

  table, input[type="text"] {
    width: 98.4%;
  }

  .button, .Button, input[type=submit] {
    width: 100%;
  }

  hr {
    display: none;
  }

  div.widget a:link, div.widget a:visited, div.widget a:active, div.widget td a:link, div.widget td a:visited, div.widget td a:active, div.widget ul li a:link, div.widget ul li a:visited, div.widget ul li a:active {
    color: #fff;
  }

  div.widget-title h3, td#left h3.tw-widgettitle, td#right h3.tw-widgettitle, td#left ul.tw-nav-list, td#right ul.tw-nav-list {
    color: #fff;
  }

  div.useCode {
    background: #404040 !important;
  }

  div#respond {
    background: #111;
  }

  a:link, a:visited, a:active {
    color: #FFFF00;
  }

  div.post table tr.alt td {
    background: #222;
  }

  div.post table td {
    background: #222;
  }

  div.post table tr.over td {
    background: #222;
  }

  .crayon-theme-familiar-learncppcom .crayon-line {
    background-color: #fff;
  }
}
