css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.nicovideo.jp") {

  /* 表示位置を動画内からコメント欄へ変更 */
  .nofix:not(.is-fullscreen) .PlayerOptionContainer {
    left: 640px!important;
    width: 384px!important;
    bottom: 0px!important;
    top: 0px!important;
  }
  /* 画面サイズ大用 */
  .nofix.is-large:not(.is-fullscreen) .PlayerOptionContainer {
    left: 854px!important;
  }
  
  .PlayerContainer {
    overflow:  unset!important;
  }
  
}
